import { useState, useEffect } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import "./App.css";
import "./styles/styles.css";
import { Container, Alert, Dropdown } from "react-bootstrap";
import MyNavbar from "./components/MyNavbar";
import MyFooter from "./components/MyFooter";
import MovieList from "./components/MovieList";
import { BrowserRouter as Router, Route } from 'react-router-dom'


const App = () => {

  // state = {
    // gallery1: [],
    // gallery2: [],
    // gallery3: [],
    // searchResults: [],
    // loading: true,
    // error: false,
  // };
  const [searchResults, useSearchResults] = useState([])
  const [loading, useLoading] = useState([])
  const [error, useError] = useState([])

  const [gallery, useGallery] = useState({   
    gallery1: [],
    gallery2: [],
    gallery3: [],
    // searchResults: [],
    // loading: true,
    // error: false,
  })



  const OMDB_URL = "http://www.omdbapi.com/?apikey=24ad60e9";

  // componentDidMount = () => {
  //   fetchMovies();
  // };

  // useEffect(() => {
	// 	fetchMovies();
	// }, [])

  const fetchMovies = () => {
    Promise.all([
      fetch(OMDB_URL + "&s=harry%20potter")
        .then((response) => response.json())
        .then((responseObject) => {
          if (responseObject.Response === "True") {
            useGallery({...gallery, gallery1 : responseObject.Search} );
          } else {
            useError( true );

          }
        }),
      fetch(OMDB_URL + "&s=avengers")
        .then((response) => response.json())
        .then((responseObject) => {
          if (responseObject.Response === "True") {
            useGallery({ ...gallery, gallery2 : responseObject.Search });
          } else {
            useError( true );
          }
        }),
      fetch(OMDB_URL + "&s=star%20wars")
        .then((response) => response.json())
        .then((responseObject) => {
          if (responseObject.Response === "True") {
           useGallery({ ...gallery, gallery3 : responseObject.Search });
          } else {
            useError( true );
          }
        }),
    ])
      .then(() => useLoading(false ))
      .catch((err) => {
       useError( true );
        console.log("An error has occurred:", err);
      });
  };

  useEffect(() => {
		fetchMovies();
	}, [])

  const showSearchResult = async (searchString) => {
    if (searchString === "") {
      // setState({ error: false, searchResults: [] }, () => {
      //   fetchMovies();
      // });
      useError(false);
      useSearchResults([])
      fetchMovies()
    } else {
      try {
        const response = await fetch(OMDB_URL + "&s=" + searchString);
        if (response.ok) {
          const data = await response.json();
          if (data.Response === "True") {
            useSearchResults(data.Search);
            useError(false);
          } else {
            useError(true);
          }
        } else {
          useError(true );
          console.log("an error occurred");
        }
      } catch (error) {
        useError( error);
        console.log(error);
      }
    }
  };


    return (
      <div>
        <MyNavbar showSearchResult={showSearchResult} />
        <Container fluid className="px-4">
          <div className="d-flex justify-content-between">
            <div className="d-flex">
              <h2 className="mb-4">TV Shows</h2>
              <div className="ml-4 mt-1">
                <Dropdown>
                  <Dropdown.Toggle
                    style={{ backgroundColor: "#221f1f" }}
                    id="dropdownMenuButton"
                    className="btn-secondary btn-sm dropdown-toggle rounded-0"
                  >
                    Genres
                  </Dropdown.Toggle>
                  <Dropdown.Menu bg="dark">
                    <Dropdown.Item href="#/action-1">Comedy</Dropdown.Item>
                    <Dropdown.Item href="#/action-2">Drama</Dropdown.Item>
                    <Dropdown.Item href="#/action-3">Thriller</Dropdown.Item>
                  </Dropdown.Menu>
                </Dropdown>
              </div>
            </div>
            <div>
              <i className="fa fa-th-large icons"></i>
              <i className="fa fa-th icons"></i>
            </div>
          </div>
          {error && (
            <Alert variant="danger" className="text-center">
              An error has occurred, please try again!
            </Alert>
          )}
         { searchResults?.length > 0 && (
            <MovieList
              title="Search results"
              movies={searchResults}
            />
          )}
          {!error && !searchResults?.length > 0 && (
            <>
              <MovieList
                title="Harry Potter"
                loading={loading}
                movies={gallery1.slice(0, 6)}
              />
              <MovieList
                title="The Avengers"
                loading={loading}
                movies={gallery2.slice(0, 6)}
              />
              <MovieList
                title="Star Wars"
                loading={loading}
                movies={gallery3.slice(0, 6)}
              />
            </>
          )}
          <MyFooter />
        </Container>
      </div>
    );

}

export default App;
